import {$authHost, $host} from "./index";

export const createFeedback = async ({name, description, id, img}) => {
    const {data} = await $authHost({method: 'POST', url: 'api/feedback', data: {name, description, id, img}});
    return data;
}

export const fetchFeedback = async (page, limit = 9) => {
    const {data} = await $host.get('api/feedback', {params: {
            page, limit
        }});
    return data;
}

export const fetchOneFeedback = async (id) => {
    const {data} = await $host.get(`api/feedback/${id}`);
    return data;
}

export const getAllFeedbacks = async (name, page = 1) => {
    const {data} = await $authHost({method:'GET', url:`api/feedback/search?page=${page}&name=${name}`});
    return data;
}
