import {$authHost, $host} from "./index";

export const createFeedback = async (feedback) => {
    const {data} = await $authHost.post('api/feedback', feedback);
    return data;
}

export const fetchFeedback = async (page, limit = 9) => {
    const {data} = await $host.get('api/feedback', {params: {
            page, limit
        }});
    return data;
}

export const fetchOneFeedback = async (id) => {
    const {data} = await $host.get(`api/feedback/${id}`);
    return data;
}

