import React, {useContext} from 'react';
import {Button, Col, Image} from "react-bootstrap";
import shop_cart from "../../assets/shopping-baskett.png";
import {NavLink} from "react-router-dom";
import {Context} from "../../index";
import {observer} from "mobx-react-lite";
import {BASKET_ROUTE, DEVICE_ROUTE} from "../../utils/consts";
import {useHistory} from 'react-router-dom';

const BasketNavBar = observer(() => {
    const {basket} = useContext(Context);
    const history = useHistory();

    return (
        <div className="d-flex align-items-center mr-3">
            <Button md={3} onClick={() => history.push(BASKET_ROUTE)}>
                {/*<NavLink to={BASKET_ROUTE} className="d-flex align-items-center">*/}

                    <Image src={shop_cart} style={{width: "100%", maxWidth: 30}} alt="basket"/>
                    <div className="ml-2" style={{textDecoration: "none", color: "white"}}>{basket.Price} РУБ</div>


            {/*</NavLink>*/}
            </Button>
        </div>
    );
});
export default BasketNavBar;
