import {Button, Nav} from "react-bootstrap";
import React, {useContext} from "react";
import {Context} from "../../../index";
import {useHistory} from "react-router-dom";
import {ADMIN_SERVICE_ROUTE, ADMIN_PHOTO_ROUTE, ORDERS_ROUTE} from "../../../utils/consts";
import BasketNavBar from "../BasketNavBar";
import {Row, Col} from "react-bootstrap";

const TrueAuth = () => {
    const {user, basket} = useContext(Context);
    const history = useHistory();

    const logOut = () => {
        user.setUser({});
        user.setIsAuth(false);
        localStorage.removeItem('token');
        basket.resetBasket();
    }

    return (
        <Nav style={{color: "white"}}>
            <Row>
                <Col>
                <BasketNavBar/>
                </Col>

                <Col>
            {user.isAuth && user.User.role === "ADMIN" && <Button
                className={"mr-3"}
                variant={"outline-light"}
                onClick={() => {history.push(ORDERS_ROUTE)}}
            >
                Заявки
            </Button>}
                </Col>

                <Col>
            <Button

                variant={"outline-light"}
                onClick={() => {history.push(ADMIN_PHOTO_ROUTE)}}
            >
               Админ панель
            </Button>
                </Col>

                <Col>
            <Button
                variant={"outline-light"}
                onClick={() => logOut()}
            >
                Выйти
            </Button>
                </Col>
            </Row>

        </Nav>
    );
};

export default TrueAuth;
