import React, {useEffect, useContext, useState} from 'react';
import {Context} from "../index";
import {observer} from "mobx-react-lite";
import {useHistory} from "react-router-dom";
import {
    Button,
    Col,
    Container,
    Dropdown,
    Form,
    Image,
    InputGroup,
    ListGroup,
    Pagination,
    Row
} from "react-bootstrap";

import CreateFeedback from "../components/modals/CreateFeedback";
import {fetchOneFeedback, getAllFeedbacks} from "../http/feedbackAPI";
import {NavLink} from "react-router-dom";

const Feedback = observer(() => {

    const {feedback} = useContext(Context);

    const history = useHistory();
    const [feedbackVisible, setFeedbackVisible] = useState(false);
    const [searchFeedback, setSearchFeedback] = useState('');
    const [searchedFeedback, setSearchedFeedback] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [count, setCount] = useState(1);
    const [successMsg, setSuccessMsg] = useState('');
    const [showSuccessMsg, setShowSuccessMsg] = useState(false);



    //pagination
    const limit = 5;
    const pageCount = Math.ceil(Number(count) / limit);
    const pages = [];
    for (let number = 1; number < pageCount + 1; number++) {
        pages.push(
            <Pagination.Item key={number} active={number === currentPage} onClick={() => setCurrentPage(number)}>
                {number}
            </Pagination.Item>
        );
    }

    useEffect(() => {
        fetchFeedback(null, 1, 100).then(data => {
            feedback.setFeedbacks(data.rows);
            feedback.setTotalCount(data.count);
        });
    }, []);




    // useEffect(() => {
    //     getAllFeedbacks(searchFeedback, currentPage).then(({count, rows}) => {
    //         setSearchedFeedback(rows);
    //         setCount(count)
    //     })
    // }, [currentPage])

    // useEffect(() => {
    //     getAllFeedbacks(searchFeedback, 1).then(({count, rows}) => {
    //         setSearchedFeedback(rows);
    //         setCount(count);
    //         setCurrentPage(1);
    //     })
    // }, [successMsg])

    // const showSuccessMsgFunc = (msg) => {
    //     setSuccessMsg(msg);
    //     setShowSuccessMsg(true);
    //     setTimeout(() => setShowSuccessMsg(false), 5000);
    // }


    const fetchFeedback = () => {
        getAllFeedbacks(searchFeedback, currentPage).then(({count, rows}) => {
            setSearchedFeedback(rows);
            setCount(count)
        })
    };

    return (
        <Container className="d-flex flex-column mt-3">
           {/* {showSuccessMsg && <Row className='d-flex, align-items-center justify-content-center'><div style={{background: '#28a745', marginLeft: 15, marginBottom: 15, borderRadius: 5, color: 'white', padding: 6}}>
                {successMsg}
            </div>
            </Row>} */}


            <Row>
                <Col md={2}>
                        <Button
                            onClick={() => setFeedbackVisible(true)}
                            variant="outline-dark"
                            className="mt-2 p-2"

                        >
                        Добавить отзыв
                        </Button>
                        <CreateFeedback show={feedbackVisible} onHide={() => setFeedbackVisible(false)}/>
                </Col>
                <Col md={10}>
                    <ListGroup>
                        {searchedFeedback && searchedFeedback.map( ({id, img, name, description}) => {
                            return (
                                <ListGroup.Item className="mt-3" key={id}>
                                    <Row>
                                        <Col xs={2}>
                                            <Image width={140} src={process.env.REACT_APP_API_URL + img}/>
                                        </Col>
                                        <Col xs={8}>
                                            <Row>
                                                <Col xs={12}>
                                                   id: {id}
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col xs={12}>
                                                    Имя: {name}
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col xs={12}>
                                                    Описание: {description}
                                                </Col>
                                            </Row>

                                        </Col>
                                    </Row>
                                </ListGroup.Item>
                            )
                        })}
                    </ListGroup>

                    <Pagination size="sm" className="mt-4 mb-4" style={{margin: "0 auto"}}>
                        {searchedFeedback && searchedFeedback.length > 0 ? pages : false}
                    </Pagination>
                </Col>
            </Row>
        </Container>
    );
});

export default Feedback;
