import React, {useEffect, useState} from 'react';
import './Portfolio.css'
import { SRLWrapper} from "simple-react-lightbox";
import Masonry from "react-masonry-css";

const images = [
    { id: '1', imageName: 'img1.jpeg', tag: 'автомобили' },
    { id: '2', imageName: 'img2.jpeg', tag: 'пейзаж' },
    { id: '3', imageName: 'img3.jpeg', tag: 'архитектура' },
    { id: '4', imageName: 'img4.jpeg', tag: 'автомобили' },
    { id: '5', imageName: 'img5.jpeg', tag: 'пейзаж' },
    { id: '6', imageName: 'img6.jpeg', tag: 'архитектура' },
    { id: '7', imageName: 'img7.jpeg', tag: 'автомобили' },
    { id: '8', imageName: 'img8.jpeg', tag: 'пейзаж' },
    { id: '9', imageName: 'img9.jpeg', tag: 'архитектура' },
    { id: '10', imageName: 'img10.jpeg', tag: 'автомобили' },
    { id: '11', imageName: 'img11.jpeg', tag: 'пейзаж' },
    { id: '12', imageName: 'img12.jpeg', tag: 'архитектура' },
    { id: '13', imageName: 'img13.jpeg', tag: 'автомобили' },
    { id: '14', imageName: 'img14.jpeg', tag: 'пейзаж' },
    { id: '15', imageName: 'img15.jpeg', tag: 'архитектура' },
    { id: '16', imageName: 'img16.jpeg', tag: 'автомобили' },
    { id: '17', imageName: 'img17.jpeg', tag: 'пейзаж' },
    { id: '18', imageName: 'img18.jpeg', tag: 'архитектура' },
    { id: '19', imageName: 'img19.jpeg', tag: 'автомобили' },
    { id: '20', imageName: 'img20.jpeg', tag: 'пейзаж' },
]

const Portfolio = () => {
    const [tag, setTag] = useState('все')
    const [ filteredImages, setFilteredImages] = useState([])

    useEffect(() => {
        tag === 'все' ? setFilteredImages(images) : setFilteredImages(images.filter(image => image.tag === tag));
    }, [tag])

    const breakpoints = {
        default: 4,
        1100: 2,
        700: 1
    }

    return (
        <div className="App">
            <div className="tags_">
                <TagButton name="все" tagActive={tag === 'все' ? true : false} handleSetTag={setTag} />
                <TagButton name="автомобили" tagActive={tag === 'автомобили' ? true : false} handleSetTag={setTag} />
                <TagButton name="пейзажи" tagActive={tag === 'пейзажи' ? true : false} handleSetTag={setTag} />
                <TagButton name="архитектура" tagActive={tag === 'архитектура' ? true : false} handleSetTag={setTag} />
            </div>

            <SRLWrapper>
                <div className="container_">
                    <Masonry
                        breakpointCols={breakpoints}
                        className="my-masonry-grid"
                        columnClassName="my-masonry-grid_column"
                    >
                        {filteredImages.map(image => (
                            <div key={image.id} className="image-card_">
                                <a href={`/images/${image.imageName}`}>
                                    <img className="image_" src={`/images/${image.imageName}`} alt="" />
                                </a>
                            </div>
                        ))}
                    </Masonry>
                </div>
            </SRLWrapper>
        </div>
    );
};

const TagButton = ({ name, handleSetTag, tagActive }) => {
    return (
        <button className={`tag_ ${tagActive ? 'active_' : null}`} onClick={() => handleSetTag(name)}>
            {name.toUpperCase()}
        </button>
    );
};

export default Portfolio;
