import React, {useContext, useState} from 'react';
import {Button, Col, Container, Form, Row} from "react-bootstrap";
import {Context} from "../index";
import {useHistory} from "react-router-dom";
import {sendOrder} from "../http/ordersAPI";
import {SHOP_ROUTE} from "../utils/consts";

const Contact = () => {
    const {basket, user} = useContext(Context);
    const [phone, setPhone] = useState(null);
    const history = useHistory();

    const buy = () => {
        let order = {
            mobile: phone,
            basket: basket.Basket
        }

        if(user.isAuth) {
            order.auth = true;
        }

        sendOrder(order).then(data => {
            console.log(data);
            basket.setDeleteAllServiceFromBasket();
            history.push(SHOP_ROUTE);
        });
    }
    return (
        <Container>
            <div>
                <h1>Хотите съемку?</h1>
                <h2 style={{width: 330, fontSize: 20, fontWeight: 300}}>Оставьте заявку и я свяжусь с вами в ближайщее время?</h2>
            </div>

            <h1 style={{marginTop: 30}}>Оформите заявку</h1>
            <Form>
                <Form.Control
                    placeholder="Введите имя..."
                    style={{marginTop: 20, width: 150}}
                />
                <Form.Control
                    placeholder="Введите свой телефон..."
                    value={phone}
                    onChange={e => setPhone(e.target.value)}
                    style={{marginTop: 20, width: 210}}
                />
                <Form.Control
                    placeholder="Введите почту или ссылку на соц.сеть..."
                    style={{marginTop: 20, width: 350}}
                />
            </Form>
            <Row className="mt-3">
                <Col xs={12}>
                    <Button variant="success" onClick={buy}>Отправить</Button>
                </Col>
            </Row>



        </Container>

    );
};

export default Contact;
