import React from 'react';
import {Card, Col, Image} from "react-bootstrap";

import {useHistory} from 'react-router-dom';
import {DEVICE_ROUTE} from "../utils/consts";

const DeviceItem = ({device}) => {
    const history = useHistory();
    console.log(device);
    return (
        <Col md={2} onClick={() => history.push(DEVICE_ROUTE + '/' + device.id)}>
            <Card style={{marginTop: 26, cursor: 'pointer', background: "#212529", borderRadius: 15}} border={"light"}>
                <Image style={{borderTopRightRadius: 15, borderTopLeftRadius: 15}}  src={process.env.REACT_APP_API_URL + device.img}/>
                <div style={{marginLeft: 16, marginTop: 16, color: "white", fontWeight: "bold", fontSize: 18}}>
                    {device.name}
                </div>
                {/*<div style={{marginTop: 5, marginLeft: 16,  color: "white", fontSize: 14, width: 280}}>*/}
                {/*    {service.rating}*/}
                {/*</div>*/}
                <div style={{marginLeft: 16, marginBottom: 16, marginTop: 5, color: "white"}}>
                    {device.price}
                </div>
            </Card>
        </Col>
    );
};

export default DeviceItem;
