import React, {useContext, useState} from 'react';
import {Button, Col, Container, Form, Image, Row} from "react-bootstrap";
import {Context} from "../index";
import {useHistory} from "react-router-dom";
import {sendOrder} from "../http/ordersAPI";
import {SHOP_ROUTE} from "../utils/consts";
import photographer from "../assets/photographer.jpg"
import phone from "../assets/phone.png"

const Contact = () => {
    const {basket, user} = useContext(Context);
    const history = useHistory();

    const buy = () => {
        let order = {
            mobile: phone,
            basket: basket.Basket
        }

        if(user.isAuth) {
            order.auth = true;
        }

        sendOrder(order).then(data => {
            console.log(data);
            basket.setDeleteAllServiceFromBasket();
            history.push(SHOP_ROUTE);
        });
    }
    return (
        <Container>
            <Row>
            <Col md={6}>
            <div style={{marginTop: 30}}>
                <h1>Хотите съемку?</h1>
                <h2 style={{width: 330, fontSize: 20, fontWeight: 300}}>Оставьте заявку и я свяжусь с вами в ближайщее время.</h2>
            </div>

            <Form>
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Control type="name" placeholder="Введите имя" style={{ marginTop: 20,width: 350}} />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Control
                            type="phone"
                            placeholder="Номер телефона"
                            value={phone}
                            onChange={e => setPhone(e.target.value)}
                            style={{ width: 350}} />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Control type="email" placeholder="Почтовый адрес или ссылка на соц.сеть" style={{ width: 350}}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                        <Form.Control as="textarea" rows={3} placeholder="Ваши пожелания" style={{ width: 350}} />
                    </Form.Group>
                </Form>
            </Form>
            <Row className="mt-3">
                <Col xs={12}>
                    <Button variant="success" onClick={buy}>Отправить</Button>
                </Col>
            </Row>
            </Col>
            <Col md={6}>
                <Image src={photographer} style={{width: "80%", marginTop: 30}} />
            </Col>
            </Row>

            <div style={{marginTop: 20}}>
                <Button variant="primary">VK</Button>{''}
                <Button style={{marginLeft: 10}}variant="danger">Instagram</Button>{''}
                <Button style={{marginLeft: 10}}variant="info">Telegram</Button>{''}
                <Button  style={{marginLeft: 10}}variant="warning">
                    <Image src={phone} style={{width: "70%", maxWidth: 30}} alt="phone"/>
                    <div className="ml-2" style={{textDecoration: "none", color: "black"}}></div>
                </Button>
            </div>
        </Container>

    );
};

export default Contact;
