import React, {useContext, useState} from 'react';
import {Button, Col, Form, Row} from "react-bootstrap";
import {Context} from "../index";
import {sendOrder} from "../http/ordersAPI";
import {useHistory} from "react-router-dom";
import {SHOP_ROUTE} from "../utils/consts";

const Ordering = () => {
    const {basket, user} = useContext(Context);
    const [phone, setPhone] = useState(null);
    const history = useHistory();

    const [showMsg, setShowMsg] = useState(false);
    const [msg, setMsg] = useState("");
    const [show, setShow] = useState(false);

    const buy = () => {
        let order = {
            mobile: phone,
            basket: basket.Basket
        }

        if(user.isAuth) {
            order.auth = true;
        }

        sendOrder(order).then(data => {
            console.log(data);
            basket.setDeleteAllServiceFromBasket();
            history.push(SHOP_ROUTE)
            setShowMsg(true);
            setMsg(data);
            setTimeout(() => setShowMsg(true), 5000)


        });
    }
    return (
        <>
            {showMsg && <Row><div style={{borderBottom: 'solid', borderBottomColor: '#28a745'}}>
                {msg}
            </div>
            </Row>}

            <h1 style={{marginTop: 30}}>Оформление заявки</h1>
            <Form>
                <Form.Group className="mb-3">
                    <Form.Control type="name" placeholder="Введите имя" style={{ marginTop: 20,width: 350}} />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Control
                        type="phone"
                        placeholder="Номер телефона"
                        value={phone}
                        onChange={e => setPhone(e.target.value)}
                        style={{ width: 350}} />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Control type="email" placeholder="Почтовый адрес или ссылка на соц.сеть" style={{ width: 350}}/>
                </Form.Group>
                <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                    <Form.Control as="textarea" rows={3} placeholder="Ваши пожелания" style={{ width: 350}} />
                </Form.Group>
            </Form>
            <Row className="mt-3">
                <Col xs={12}>
                    <Button variant="success" onClick={buy}>Отправить</Button>
                </Col>
            </Row>
        </>
    );
};

export default Ordering;
