import React, {useContext, useEffect, useState} from 'react';
import {Card, Col, Image, Form, Modal, Button} from "react-bootstrap";
import {useParams} from 'react-router-dom';
import {fetchDeletePhoto, fetchOnePhoto} from "../http/photoAPI";
import {PHOTO_ROUTE} from "../utils/consts";
import {useHistory} from 'react-router-dom';
import {Context} from "../index";
const PhotoItem = ({photo}) => {

    const history = useHistory();

    const {id} = useParams();
    const [photoCurr, setPhotoCurr] = useState({});
    const [selectAlbum, setSelectAlbum] = useState({});
    const [img, setImg] = useState("");
    const [isDisabledPutBtn, setDisabledPutBtn] = useState(true);

    const deletePhoto = () => {
        fetchDeletePhoto(id).then(() => {
            history.push(PHOTO_ROUTE);
        })
    }




    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <Col md={4} >
            <Card style={{marginTop: 15, cursor: 'pointer', background: "#212529", borderRadius: 15}} border={"light"}>
                <Image style={{borderTopRightRadius: 15, borderTopLeftRadius: 15}}  src={process.env.REACT_APP_API_URL + photo.img}/>

            </Card>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Удалить фото {photoCurr.id}?</Modal.Title>
                </Modal.Header>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Отмена
                    </Button>
                    <Button variant="primary" onClick={deletePhoto}>
                        Удалить
                    </Button>
                </Modal.Footer>
            </Modal>
        </Col>
    );
};

export default PhotoItem;
