import React, {useContext, useState} from 'react';
import {Button, Col, Container, Form, Image, Row, Popover, useRef, Overlay} from "react-bootstrap";
import {Context} from "../index";
import {useHistory} from "react-router-dom";
import {sendOrder} from "../http/ordersAPI";
import {SHOP_ROUTE} from "../utils/consts";
import photographer from "../assets/photographer.jpg"
import phones from "../assets/phones.png";


const Contact = () => {
    const {basket, user} = useContext(Context);
    const [phone, setPhone] = useState(null);
    const history = useHistory();

    const [showMsg, setShowMsg] = useState(false);
    const [msg, setMsg] = useState("");

    const buy = () => {
        let order = {
            mobile: phone,
            basket: basket.Basket
        }

        if(user.isAuth) {
            order.auth = true;
        }

        sendOrder(order).then(data => {
            console.log(data);
            basket.setDeleteAllServiceFromBasket();
            setShowMsg(true);
            setMsg(data);
            setTimeout(() => setShowMsg(true), 5000)
        });
    }

    const [show, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);

  const handleClick = (event) => {
    setShow(!show);
    setTarget(event.target);
  };

    return (
        <Container className="mt-3">
            {showMsg && <Row className='d-flex, align-items-center justify-content-center'><div style={{background: '#28a745', marginLeft: 15, borderRadius: 5, color: 'white', padding: 6}}>
                {msg}
            </div>
            </Row>}
            <Row>
            <Col md={6}>
            <div style={{marginTop: 30}}>
                <h1>Хотите съемку?</h1>
                <h2 style={{width: 330, fontSize: 20, fontWeight: 300}}>Оставьте заявку и я свяжусь с вами в ближайщее время.</h2>
            </div>


                <Form>
                    <Form.Group className="mb-3">
                        <Form.Control type="name" placeholder="Введите имя" style={{ marginTop: 20,width: 350}} />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Control
                            type="phone"
                            placeholder="Номер телефона"
                            value={phone}
                            onChange={e => setPhone(e.target.value)}
                            style={{ width: 350}} />
                    </Form.Group>
                    <Form.Group className="mb-3">
                        <Form.Control type="email" placeholder="Почтовый адрес или ссылка на соц.сеть" style={{ width: 350}}/>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                        <Form.Control as="textarea" rows={3} placeholder="Ваши пожелания" style={{ width: 350}} />
                    </Form.Group>
                </Form>

            <Row className="mt-3">
                <Col xs={12}>
                    <Button variant="success" onClick={buy}>Отправить</Button>
                </Col>
            </Row>

                <div style={{marginTop: 20}}>
                    <a href="https://vk.com/dinyyyaa"><Button variant="primary">VK</Button>{''}</a>
                    <a href="https://t-do.ru/dinyyyaa"><Button style={{marginLeft: 10}}variant="danger">Instagram</Button>{''}</a>
                    <a href="https://t-do.ru/dinyyyaa"><Button style={{marginLeft: 10}}variant="info">Telegram</Button>{''}</a>
                    <Button  style={{marginLeft: 10}}variant="warning">
                        <Image src={phones} style={{width: "70%", maxWidth: 30}} alt="phone"/>
                        <div className="ml-2" style={{textDecoration: "none", color: "black"}}></div>
                    </Button>
                </div>

                <div ref={ref}>
      <Button onClick={handleClick}>Holy guacamole!</Button>

      <Overlay
        show={show}
        target={target}
        placement="bottom"
        container={ref}
        containerPadding={20}
      >
        <Popover id="popover-contained">
          <Popover.Header as="h3">Popover bottom</Popover.Header>
          <Popover.Body>
            <strong>Holy guacamole!</strong> Check this info.
          </Popover.Body>
        </Popover>
      </Overlay>
    </div>



            </Col>
            <Col md={6}>
                <Image src={photographer} style={{width: "80%", marginTop: 30}} />
            </Col>
            </Row>




        </Container>

    );
};

export default Contact;
